import React, { useState } from "react";
import InputReceipt from "./InputReceipt";

import "./ReceiptForm.css";

const ReceiptForm = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [enteredSource, setEnteredSource] = useState("");
  const [enteredIngredients, setEnteredIngredients] = useState([]);
  const [enteredTime, setEnteredTime] = useState("");
  const [enteredInstructions, setEnteredInstructions] = useState("");

  const nameChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  // const sourceChangeHandler = (event) => {
  //   setEnteredSource(event.target.value);
  // };

  const ingredientsChangeHandler = (event) => {
    setEnteredIngredients(event.target.value);
  };

  const timeChangeHandler = (event) => {
    setEnteredTime(event.target.value);
  };

  const instructionsChangeHandler = (event) => {
    setEnteredInstructions(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const receiptData = {
      name: enteredName,
      source: enteredSource,
      ingredients: enteredIngredients,
      time: enteredTime,
      instructions: enteredInstructions,
    };

    props.onSaveReceiptData(receiptData);
    setEnteredName("");
    setEnteredSource("");
    setEnteredIngredients([]);
    setEnteredTime("");
    setEnteredInstructions("");
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-receipt__controls">
        <div className="new-receipt__control">
          <label>Name</label>
          <input type="text" value={enteredName} onChange={nameChangeHandler} />
        </div>
        <div className="new-receipt__control">
          <label>Receipt Source</label>
          <InputReceipt />
          {/* <input
            type="text"
            value={enteredSource}
            onChange={sourceChangeHandler}
          /> */}
        </div>
        <div className="new-receipt__control">
          <label>Ingredients</label>
          <input
            type="text"
            value={enteredIngredients}
            onChange={ingredientsChangeHandler}
          />
        </div>
        <div className="new-receipt__control">
          <label>Preparation Time</label>
          <input type="text" value={enteredTime} onChange={timeChangeHandler} />
        </div>
        <div className="new-receipt__control">
          <label>Preparation Instruction</label>
          <input
            type="text"
            value={enteredInstructions}
            onChange={instructionsChangeHandler}
          />
        </div>
      </div>
    </form>
  );
};

export default ReceiptForm;

// Recipe name
// - Recipe source (where we have obtained the recipe)
// - List of ingredients
// - Preparation time
// - Preparation instructions.
