import React from "react";
import { connect } from "react-redux";

let ReceiptForm = ({ dispatch }) => {
  return (
    <form
      onSubmit={(event) => {
        event.preventDefault();
      }}
    >
      <div>
        <input type="text" placeholder="Receipt Name" value={name} {...name} />
      </div>
      <div>
        <input type="text" placeholder="Receipt Source" {...source} />
      </div>
      <div>
        <select multiple={true}>
          <option>Tomato</option>
          <option>Potato</option>
          <option>Onion</option>
        </select>
      </div>
      <div>
        <input type="time" placeholder="Preparation Time" {...time} />
      </div>
      <div>
        <input
          type="text"
          placeholder="Preparation Instruction"
          {...instructions}
        />
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default connect()(ReceiptForm);

