import { combineReducers } from "redux";
import {
  RECEIVE_RECEIPTS,
  REQUEST_RECEIPTS,
  START_RECEIPT_ADD,
  ADD_RECEIPT,
} from "./Constants";
import { reducer as formReducer } from "redux-form";

const mainReducer = (
  state = {
    receipts: [],
    loadingReceipts: true,
    addingReceipt: false,
  },
  action
) => {
  switch (action.type) {
    case RECEIVE_RECEIPTS:
      return Object.assign({}, state, {
        receipts: action.receipts,
        loadingReceipts: false,
      });
    case REQUEST_RECEIPTS:
      return Object.assign({}, state, {
        loadingReceipts: true,
      });
    case START_RECEIPT_ADD:
      return Object.assign({}, state, {
        addingReceipt: true,
      });
    case ADD_RECEIPT:
      return Object.assign({}, state, {
        addingReceipt: false,
        receipts: [...state.receipts, action.receipt],
      });
    default:
      return state;
  }
};

const reducer = combineReducers({
  main: mainReducer,
  form: formReducer,
});

export default reducer;
