import React from "react";
import { connect } from "react-redux";
import { addReceipt } from "../Redux/Actions";

let ReceiptForm = ({ dispatch }) => {
  let form;
  return (
    <form
      ref={(node) => (form = node)}
      onSubmit={(event) => {
        event.preventDefault();
        const receipt = {
          id: new Date().getTime(),
          name: form.name.value,
          source: form.source.value,
          ingridients: Array.from(
            form.ingridients.selectedOptions,
            (option) => option.value
          ),
          time: form.time.value,
          instructions: form.instructions.value,
        };
        dispatch(addReceipt(receipt));
      }}
    >
      <div>
        <input type="text" placeholder="Receipt Name" name="name" />
      </div>
      <div>
        <input type="text" placeholder="Receipt Source" name="source" />
      </div>
      <div>
        <select name="ingridients" multiple>
          <option></option>
          <option>Flour</option>
          <option>Milk</option>
          <option>Oil</option>
          <option>Salt</option>
          <option>Sugar</option>
          <option>Eggs</option>
          <option>Tomatoes</option>
          <option>Peppers</option>
          <option>Cheese</option>
          <option>Potatoes</option>
          <option>Meat</option>
        </select>
      </div>
      <div>
        <input type="time" placeholder="Preparation Time" name="time" />
      </div>
      <div>
        <input
          type="text"
          placeholder="Preparation Instruction"
          name="instructions"
        />
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default connect()(ReceiptForm);

