import React, { useState } from "react";

import ReceiptForm from "./ReceiptForm";

const NewReceipt = (props) => {
  const [isEditing, setIsEditing] = useState(false);

  const saveReceiptDataHandler = (enteredReceiptData) => {
    const receiptData = {
      ...enteredReceiptData,
      id: Math.random().toString(),
    };
    props.onAddReceipt(receiptData);
    setIsEditing(false);
  };

  const startEditingHandler = () => {
    setIsEditing(true);
  };

  const stopEditingHandler = () => {
    setIsEditing(false);
  };

  return (
    <div className="new-receipt">
      {!isEditing && (
        <button onClick={startEditingHandler}>Add New Receipt</button>
      )}
      {isEditing && (
        <ReceiptForm
          onSaveReceiptData={saveReceiptDataHandler}
          onCancel={stopEditingHandler}
        />
      )}
    </div>
  );
};

export default NewReceipt;
