import {
  RECEIVE_RECEIPTS,
  REQUEST_RECEIPTS,
  START_RECEIPT_ADD,
  ADD_RECEIPT,
} from "./Constants";

export function requestReceipts() {
  return (dispatch) => {
    dispatch({
      type: REQUEST_RECEIPTS,
    });
  };
}

export function receiveReceipts(receipts) {
  return {
    type: RECEIVE_RECEIPTS,
    receipts,
  };
}

export function startReceiptAdd() {
  return {
    type: START_RECEIPT_ADD,
  };
}

export function addReceipt() {
  return (dispatch, getState) => {
    const form = getState().form;
    const receipt = {
      name: form.receipt.name.value,
      source: form.receipt.source.value,
      time: form.receipt.time.value,
    };
    dispatch({
      type: ADD_RECEIPT,
      receipt,
    });
  };
}
